package shuwei.leetcode.cn.lcp.one.nine;

import org.junit.Assert;
import org.junit.Test;

public class SolutionTest {
    @Test
    public void test() {
        Solution s = new Solution();
        Assert.assertEquals(2, s.minimumOperations("rrryyyrryyyrr"));
        long start = System.currentTimeMillis();
        System.out.println(s.minimumOperations(""));
        System.out.println(System.currentTimeMillis() - start);
        Assert.assertEquals(3, s.minimumOperations("yry"));
//        Assert.assertEquals(2, s.minimumOperations("rrryyyrryyyrr"));
        Assert.assertEquals(0, s.minimumOperations("ryr"));
    }
}
